import React from 'react';
import { useContext } from 'react';
import { Store } from '../Store';
import { Helmet } from 'react-helmet-async';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
//import MessageBox from '../components/MessageBox';
import { NavLink } from 'react-router-dom';
import ListGroup from 'react-bootstrap/ListGroupItem';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function CartScreen() {

    const { state, dispatch: ctxDispatch } = useContext(Store);
    const { cart:
        { cartItems },
    } = state;

    return (



        <>
            <div>
                <ListGroup>
                    {cartItems.map((item) => (
                        <ListGroup.Item key={item._id}>
                            <Row className='align-items-center'>
                                <Col md={4}>
                                    <img
                                        src={item.image}
                                        alt={item.name}
                                        className='img-fluid rounder img-thumbnail'
                                    ></img>
                                    <NavLink to={`/product/${item.slug}`}>{item.name}</NavLink>
                                </Col>
                                <Col md={3}>
                                    <Button variant="light" disabled={item.quantity === 1}>
                                        <i className='fas fa-minus-circle'></i>
                                    </Button>{' '}
                                    <span>{item.quantity}</span>{' '}
                                    <Button variant="light" disabled={item.quantity === 1}>
                                        <i className='fas fa-plus-circle'></i>
                                    </Button>
                                    {/*<button onClick={addToCartHandler} className="Button">*/}
                                </Col>
                                <Col md={3}>${item.price}</Col>
                                <Col md={2}>
                                    <Button variant="light">
                                        <i className="fas fa-trash"></i>
                                    </Button>
                                </Col>
                            </Row>
                        </ListGroup.Item>
                    ))}
                </ListGroup>
                <Col md={4}>
                    <Card>
                        <Card.Body>
                            <ListGroup variant="flush">
                                <ListGroup.Item>
                                    <h3>Subtotal(
                                        {cartItems.reduce((a, c) => a + c.quantity, 0)}{' '}
                                        items) : $
                                        {cartItems.reduce((a, c) => a + c.quantity * c.price, 0)}
                                    </h3>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <div className="d-grid">
                                        <Button
                                            type="button"
                                            variant="primary"
                                            disabled={cartItems.length === 0}
                                        >Proced to Checkout</Button>
                                    </div>
                                </ListGroup.Item>
                            </ListGroup>
                        </Card.Body>
                    </Card>
                </Col>
            </div>
        </>


    )
}
export default CartScreen;
