import { Helmet } from "react-helmet-async";
import { useLocation } from "react-router-dom";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import { NavLink } from "react-router-dom";


export default function SiginScreen() {
    const { search } = useLocation();
    const redirectInUrl = new URLSearchParams(search).get('redirect');
    const redirect = redirectInUrl ? redirectInUrl : '/';


    return (
        <Container className="small-container">
            <Helmet>
                <title>Sign In</title>
            </Helmet>
            <h1>Sign In</h1>
            <Form>
                <Form.Group className="mb-3" controlId="email">
                    <Form.Label>Email</Form.Label>
                    <Form.Control placeholder="Enter your email" type="email" required />
                </Form.Group>
                <Form.Group className="mb-3" controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control placeholder="Enter your Password" type="password" required />
                </Form.Group>
                <div>
                    <button type="submit" className="Button">Sign In</button>
                </div>
                <div className="mb-3">
                    New customer?{' '}
                    <NavLink to={`/signup?redirect=${redirect}`}>Create your account</NavLink>
                </div>
            </Form>
        </Container>
    )
}
